function _lang($char){
    #echo $char;
	global $EnLib, $PtLib, $zh_s, $zh_t, $ViLib;

	if(LANG == 't' || LANG == 's'){
		if (LANG == 't') {
		   $s1 = $zh_s;
		   $s2 = $zh_t;
		}else if(LANG == 's') {
		   $s1 = $zh_t;
		   $s2 = $zh_s;
		}
		$a = '';

		$old = mb_str_split($char);

		for($i=0;$i<count($old);$i++){
			$pos = array_search($old[$i], $s1);
			if($pos > 0){
				$a .= $s2[$pos];
			}else{
				$a .= $old[$i];
			}
		}
		return $a;
	}elseif(LANG == 'e'){
		if(array_key_exists($char, $EnLib)){
			return $EnLib[$char];
		}else{
			return $char;
		}
	}elseif(LANG == 'pt'){
		if(array_key_exists($char, $PtLib)){
			return $PtLib[$char];
		}else{
			return $char;
		}
	}elseif(LANG == 'v') {
		if(array_key_exists($char, $ViLib)){
			return $ViLib[$char];
		}else{
			return $char;
		}
	}
}

